import './App.css';
import {useState,useEffect} from "react"
import { aleatorio } from './cardsDistintos';
import Card from "./components/Card"
function App() {
  const [arr,setArr] = useState([])
  const [newArr,setNewArr] = useState([])
  const [color, setColor] = useState("black");
  const [visible, setVisible] = useState(false)
  useEffect(() => {
    fetch("http://hp-api.herokuapp.com/api/characters/")
    .then(reposta => reposta.json())
    .then(resposta => setArr(resposta.filter((e) => e.hogwartsStudent === true))).catch(err => console.log(err))

  },[])
  function numAle () {
    setNewArr(aleatorio(arr))
}
  function corAle (){

    setColor("#" + Math.floor(Math.random() * 16581375).toString(16))
}

  console.log(newArr)
  return (
    <div className="App-header">
      {
        visible ?
      <>
        <Card arr={newArr} color={color}/>
        <button className="botao" onClick={() => {numAle();corAle()}} >Tentar Novamente</button>
      </>
      :
      <div className="menuInicial">
        <h1>Torneio tribruxo</h1>
        <h4>Click no botao para encontrar os feiticeiros!</h4>
        <button className="botaoComecar" onClick={() => {setVisible(true);numAle();corAle()}} >Come√ßar!</button>
      </div>
      }
    </div>
  );
}

export default App;
