import './App.css';
import {useState,useEffect} from "react"
import Card from "./components/Card"
function App() {
  const [arr,setArr] = useState([])
  useEffect(() => {
    fetch("http://hp-api.herokuapp.com/api/characters/students").
    then(reposta => reposta.json()).
    then(resposta => setArr(resposta)).catch(err => console.log(err))

  },[])
  const [newArr,setNewArr] = useState([])
  const [numAle,setNumAle] = useState(0)
  while(newArr.length <= 2){
      setNumAle(arr[ Math.floor(Math.random() * arr.length)])
      if(!newArr.some((a) => a === numAle)){
          setNewArr([...newArr,numAle])
      }
  }
  console.log(newArr)
  return (
    <div className="App">
      <div className="App-header">
        <Card arr={arr}/>
      </div>
    </div>
  );
}

export default App;
