  const [newArr,NewArr] = useState(0)
  useEffect(() => {
    fetch("http://hp-api.herokuapp.com/api/characters/students")
    .then(reposta => reposta.json())
    .then(resposta => setArr(resposta)).catch(err => console.log(err))

  },[])
  NewArr(newArr + 1)
  console.log(newArr)
  return (
    <div className="App">
      <div className="App-header">
        <Card arr={arr}/>
      </div>
    </div>
  );
}

export default App;
