import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { Switch, Route } from "react-router";
import "./App.css";
import { useHistory } from "react-router";
import { Login } from "./pages/Login";
import { Home } from "./pages/Home";
import axios from "axios";
import { useState } from "react";

function App() {
  const [user,setUser] = useState({})
  const [mostrarMsg,setMostrarMsg] = useState("")
  const [msgSucesso,setMsgSucesso] = useState("login efetuado com sucesso!")
  const [msgErro,setMsgErro] = useState("Senha ou usuario incorretos!")
  const history = useHistory()
  const requisicao = (user) =>{
  axios.post("https://kenzieshop.herokuapp.com/sessions/",user)
      .then((response) => {
        setMostrarMsg("sucesso");
        console.log(response);
        setTimeout(() =>
          {history.push("/home")}
        ,2000);
      }).catch(setMostrarMsg("fracasso"))
  }
  const formSchema = yup.object().shape({
    username: yup.string().min(5, "usuario deve ter pelo menos 5 caracteres").required("Campo Obrigat贸rio"),
    password: yup
      .string()
      .min(4, "a senha tem menos de 4 caracteres")
      .required("Campo Obrigat贸rio"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });
  const onSubmitFunction = (data) => {
    setUser(data)
    requisicao(data)
  };




  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
          <Login handleSubmit={handleSubmit} onSubmitFunction={onSubmitFunction} register={register} errors={errors} />
          {mostrarMsg === "sucesso" && <p className="msgSucesso">{msgSucesso}</p>}
          {mostrarMsg === "fracasso" && <p className="msgErro">{msgErro}</p>}
        </Route>
        <Route exact path="/home">
          <Home user={user} />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
