import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { Switch, Route } from "react-router";
import "./App.css";
// import { useHistory } from "react-router";
import { Login } from "./pages/Login";
import { Home } from "./pages/Home";
import axios from "axios";
import { useState } from "react";

function App() {
  const [msgSucesso,setMsgSucesso] = useState({msg:"login efetuado com sucesso!",mostrar:false})
  const [msgErro,setMsgErro] = useState({msg:"Senha ou usuario incorretos!",mostrar:false})
  const requisicao = (user) =>{
  axios.post("https://kenzieshop.herokuapp.com/sessions/",user)
      .then((response) => {
        setMostrarMsg(true);
        console.log(response);
      }).catch(setMostrarMsg(true))
  }
  const formSchema = yup.object().shape({
    username: yup.string().min(5, "usuario deve ter pelo menos 5 caracteres").required("Campo Obrigat贸rio"),
    password: yup
      .string()
      .min(4, "a senha tem menos de 4 caracteres")
      .required("Campo Obrigat贸rio"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });
  const onSubmitFunction = (data) => {
    // history.push("/home");
    // setUser(data);
    requisicao(data)
  };
  // console.log(user)




  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
          <Login handleSubmit={handleSubmit} onSubmitFunction={onSubmitFunction} register={register} errors={errors} />
          {mostrarMsg ? <p>{msgSucesso}</p> : <p>{msgErro}</p>}
        </Route>
        <Route exact path="/home">
          <Home />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
