import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { Switch, Route } from "react-router";
import "./App.css";
import { useState } from "react";
// import { useHistory } from "react-router";
import { Login } from "./pages/Login";
import { Home } from "./pages/Home";
import axios from "axios";

function App() {
  const [user, setUser] = useState({});
  // const history = useHistory();
  const requisicao = (user) =>{
  axios.post("https://kenzieshop.herokuapp.com/sessions/",user)
      .then((response) => {
        console.log(response);
      }).catch(err => console.log(err))
  }
  const formSchema = yup.object().shape({
    username: yup.string().min(5, "usuario deve ter pelo menos 5 caracteres").required("Campo Obrigat贸rio"),
    password: yup
      .string()
      .min(4, "a senha tem menos de 4 caracteres")
      .required("Campo Obrigat贸rio"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });
  const onSubmitFunction = (data) => {
    // history.push("/home");
    setUser(data);
    requisicao(user)
  };
  // console.log(user)




  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
          <Login handleSubmit={handleSubmit} onSubmitFunction={onSubmitFunction} register={register} errors={errors} />
        </Route>
        <Route exact path="/home">
          <Home />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
