import styled from "styled-components"


export const Container = styled.div`

    display: flex;
    flex-direction: column;
    align-items: center;
    span{
        text-align: center;
        background: var(--azul);
        color: white;

    }
    div{
        margin-bottom: 30px ;
        font-size: 17px;
        font-weight: bold;
    }

`
export const ContainerForm = styled.div`
    border: 2px solid #F5F5F5;
    height: 426px;
    width: 320px;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;

    form{
        display: flex;
        flex-direction: column;
    }
    input{
        margin: 15px 0 0 0;
    }
    button{
        margin: 15px 0;
    }
`
