    }
    const apagarTech = () => {

    }
    useEffect(() => {
        getData()
    } ,[])

    if(!auth){
        return <Redirect to="/"/>
    }
  return (
    <Container>
      <header>
        <Titulo />
        <figure>
          <img
            src="https://images-na.ssl-images-amazon.com/images/I/71+mDoHG4mL.png"
            alt="user"
          />
        </figure>
      </header>
      <div className="minhasTecnologias tecs">
          <div className="headTecs"> <h3>Minhas tecnologias</h3>
        <img src={Add} alt="add"/> </div>
        <ModalTech />
        {user?.techs.map((e) => (<li key={e.id}>
        <h3>{e.title}</h3>
        <h5>{e.status}</h5>
        </li>))}
      </div>
      <div className="MeusTrabalhos tecs">
      <div className="headTecs"> <h3>Meus Trabalhos</h3>
        <img src={Add} alt="add"/> </div>
        {user?.works.map((e) => (<li>
        <h3>{e.title}</h3>
        <p>{e.description}</p>

        </li>))}
      </div>
      <div className="dadosUser">
      <figure>
          <div className="headUser"> <img
            src="https://images-na.ssl-images-amazon.com/images/I/71+mDoHG4mL.png"
            alt="user"
          /><div> <h3>Nome</h3> <h4>Curso</h4> <h5>detalhes</h5> </div> </div>

        </figure>
      </div>
    </Container>
  );
};

export default Home;
