import {createGlobalStyle} from "style-components"

export default createGlobalStyle`
    *{
        margin: 0;
        padding: 0;
        font-family: Inter , sans-serif;
        box-sizing: border-box;
        outline: 0;
    }
    :root{
        --azul:#403CAA;
        --verde: #11995E;
        --cinza: #333333;
        --cinzaClaro: #999999;
        --branco: #F5F5F5;

    }
    body{
        background: white;
    }
`
