import styled from "styled-components"


export const Container = styled.div`

    display: flex;
    flex-direction: column;
    align-items: center;


`
export const ContainerForm = styled.div`
    border: 2px solid #F5F5F5;
    min-height: 330px;
    width: 320px;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;

    form{
        display: flex;
        flex-direction: column;
    }
    input{
        margin: 15px 0 0 0;
        height: 40px;
        width: 275px;
        padding-left: 8px;
    }
    button{
        height: 40px;
        margin: 15px 0;
        background: var(--azul);
        color: white;
        font-weight: bold;
        border: none;
        border-radius: 8px;
    }
    .button-Cadastrar{
        background: var(--branco);
        color: var(--cinzaClaro);
    }
    .divMsg{
        font-size: 0.7rem;
        margin: 0;
        text-align: center;
        color: var(--cinzaClaro);
    }
    .divMsg p{
        color: var(--cinza);

    }
    .msgError{
        color: red;
        background: white;
    }
`
