import styled from "styled-components";

export const Container = styled.div`
  div {
    margin: 0;
  }
  display: flex;
  flex-direction: column;
  align-items: center;
  header {
    height: 80px;
    width: 85vw;
    background: #ffffff;
    box-shadow: 0px 4px 40px -10px rgba(0, 0, 0, 0.25);
    border-radius: 8px;
    display: flex;
    align-items: center;
    padding-left: 20px;
  }
  figure img{
      height: 50px;
  }
`;
