import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useState } from "react";

const ModalTech = ({adcionarTech}) => {
    const [nivelTech, setNivelTech] = useState("")
    const [user, setUser] = useState({})
  const formSchema = yup.object().shape({
    title: yup
      .string()
      .min(1, "No minimo 1 caracteres")
      .required("Campo Obrigatório"),
      status: yup
      .string()
      .required("Campo Obrigatório")
  });
  console.log(nivelTech)
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const onSubmitFunction = (data) => {
      setUser(...data,...{"status" : nivelTech})
      //   adcionarTech(user)
      // setUser(data);
    };
    console.log(user)

  return (
    <form className="form" onSubmit={handleSubmit(onSubmitFunction)}>
      <h3>Cadastrar Tecnologia</h3>
      <input placeholder="Nome da Tech" {...register("title")} />
      <span>{errors.title?.message}</span>
      <ul>
          <li onClick={() => setNivelTech("iniciante")}>iniciante</li>
          <li onClick={() => setNivelTech("intermediario")}>intermediario</li>
          <li onClick={() => setNivelTech("avançado")}>avançado</li>
      </ul>
      <button type="submit">Cadastrar</button>
    </form>
  );
};
export default ModalTech
