import Titulo from "../../components/TituloKenzie";

// import { Link } from "react-router-dom";

import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useHistory } from "react-router";
import { ContainerForm } from "./styles.js";
import { Container } from "./styles.js";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { useState } from "react";

const Home = () => {
  const history = useHistory();
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };

  const formSchema = yup.object().shape({
    nomeCompleto: yup
      .string()
      .min(18, "No minimo 18 caracteres")
      .required("Campo Obrigatório")
      .matches(
        /^([\w]{3,})+\s+([\w\s]{3,})+$/i,
        "Somente letras e o nome completo"
      ),
    email: yup.string().email("Email Invalido").required("Campo Obrigatório"),
    bio: yup
      .string()
      .min(10, "No minimo 10 caracteres")
      .required("Campo Obrigatório"),
    telefone: yup
      .string()
      .min(11, "Numero inválido")
      .required("Campo Obrigatório")
      .matches(
        /(\?\d{2}\?\s)?(\d{4,5}\d{4})/,
        "telefone não está correto o formator correto é (00) 90000-0000"
      ),
    senha: yup
      .string()
      .min(8, "Senha fraca! adcione letras, numeros e caracteres especiais")
      .required("Campo Obrigatório")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
        "Mínimo de 8 digitos, um caracter especial e uma letra maiúscula"
      ),
    confirmaSenha: yup.string().oneOf([yup.ref("senha")], "Senha não confere"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const onSubmitFunction = (data) => {
    history.push("/home");
    // setUser(data);
  };

  return (
    <Container>
      <Titulo />
      <Button onClick={handleOpen}>Open modal</Button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </Typography>
        </Box>
      </Modal>
      <ContainerForm>
        <form className="form" onSubmit={handleSubmit(onSubmitFunction)}>
          <input type="text" placeholder="Nome" {...register("nomeCompleto")} />
          <span className="msgError">{errors.nomeCompleto?.message}</span>
          <input type="text" placeholder="Email" {...register("email")} />
          <span className="msgError">{errors.email?.message}</span>
          <input type="text" placeholder="Bio" {...register("bio")} />
          <span className="msgError">{errors.bio?.message}</span>
          <input type="text" placeholder="Contato" {...register("telefone")} />
          <span className="msgError">{errors.telefone?.message}</span>
          <input
            type="password"
            placeholder="senha"
            {...register("confirmaEmail")}
          />
          <span className="msgError">{errors.senha?.message}</span>
          <ul className="lista-Modulos">
            <li>
              Primeiro <div className="modalPrimeiro msgModalModulos"><h4>Primeiro Modulo</h4>(Introduçao ao front-end)</div>
            </li>
            <li>
              Segundo <div className="modalSegundo msgModalModulos"><h4>Segundo Modulo</h4>(Front-end avançado)</div>
            </li>
            <li>
              Terceiro <div className="modalTerceiro msgModalModulos"><h4>Terceiro Modulo</h4>(Introduçao ao back-end)</div>
            </li>
            <li>
              Quarto <div className="modalQuarto msgModalModulos"><h4>Quarto Modulo</h4>(ack-end avançado)</div>
            </li>
          </ul>
          <input
            type="password"
            placeholder="Confirmar senha"
            {...register("confirmaSenha")}
          />
          <span className="msgError">{errors.confirmaSenha?.message}</span>
          <button className="cadastrar" type="submit">
            Cadastrar
          </button>
        </form>
      </ContainerForm>
    </Container>
  );
};
export default Home;
