// import { toast } from "react-toastify";
import "./styles.css";
const ModalEditarApagarTech = ({apagarTech,id}) => {
  const [nivelTech, setNivelTech] = useState("");
  const [titulo, setTitulo] = useState("");
  const [visible,setVisible] = useState(false)

  // const verificador = () => {
