import styled from "styled-components";

export const Container = styled.div`
  div {
    margin: 0;
    background: white;
  }
  display: flex;
  flex-direction: column;
  align-items: center;
  header {
    height: 80px;
    width: 90vw;
    background: #ffffff;
    box-shadow: 0px 4px 40px -10px rgba(0, 0, 0, 0.25);
    border-radius: 8px;
    display: flex;
    align-items: center;
    padding: 0 20px;
    justify-content: space-between;
  }
  figure img {
    height: 40px;
    border-radius: 100%;
  }
  .tecs {
    min-width: 290px;
    min-height: 400px;
    border-radius: 5px;
    margin-top: 30px;
  }
