import toast from "react-hot-toast";
import api from "../../../services/api";
import { addDigimon } from "./actions";

const addDigimonsThunk = (digimon) => (dispatch) => {
    api.get(`/api/digimon/name/${digimon}`).then(resp => dispatch(addDigimon(resp.data))).
    catch(() => toast.error("This didn't work."))
 //Aqui faça uma requisição com o axios e em seguida, no .then()
 //utilize a função dispatch() passando addDigimon(response.data[0].name)
}
export default addDigimonsThunk
