import { ADD_DIGIMONS } from "./actionTypes";

const digimonsReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_DIGIMONS:
        console.log(action)
		return [...state.digimon,action.digimon]
    default:
      return state;
  }
};

export default digimonsReducer;
