import { useSelector } from "react-redux";
import kenzie from "../../assets/kenzie.jpeg"
const User = () => {
  const { comments } = useSelector((state) => state.user);
  console.log(comments);
  return (
    <div>
        <img src={kenzie} alt="kenzie" />
      <ul>
        {comments.map((e,i) => (
          <li key={i}> {e} </li>
        ))}
      </ul>
    </div>
  );
};

export default User;
