import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import "./styles.css";
const Header = ({produto}) => {
    const [total, setTotal] = useState(0)
    const history = useHistory()
    useEffect(() => {
        setTotal(total + 1)
    },[produto, total])
  return (
    <header>
      <h1>Kenzie shop</h1>
      <div className="controles">
          <span>{total}</span>
        <span className="irCarrinho" onClick={() => history.push("/carrinho")} >Carrinho</span>
        <span>Entrar</span>
      </div>
    </header>
  );
};

export default Header;
