import { useDispatch } from "react-redux"
import { dataProdutos } from "../../assets/dataProducts"
const ListProducts = () => {
    const dispatch = useDispatch()
    return(
        <div>
            <ul>
                {dataProdutos.map(e => <li key={e.id}>
                    <figure>
                        <img src={e.imageUrl} alt={e.produtoNome} />
                        <figcaption>{e.produtoNome}</figcaption>
                    </figure>
                    <p>{e.preco}</p>
                    <button onClick={() => dispatch(e)}>Adcionar ao carrinho</button>
                </li> )}
            </ul>
        </div>
    )
}

export default ListProducts
