const Carrinho = ({produto}) => {
    console.log(produto)
    return(
        <div>
            <h3>Resumo do pedido</h3>
            <div>
                <h4>{produto.length} Produtos</h4>
                <p>{`R$${produto.reduce((a,e) => a+Number(e.preco),0).toFixed(2)}`}</p>
            </div>
        </div>
    )
}

export default Carrinho
